public class Date15_Ternary {

	public static void main(String[] args) {

		// TODO Auto-generated method stub
int g;
		g=(5<7)?2:3;
		System.out.println("Value " +g);

	int a=5,b=3,c=7;
	int d= (a!=b)?(c<b)?b:(b>a)?a : b : c;
	System.out.print("Value" +d);
	}
}	

